package com.meirusfandi.searchmovie.data;import com.meirusfandi.searchmovie.BuildConfig;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Query;public interface MovieInterface {    @GET(BuildConfig.SEARCH_MOVIE)    Call<MovieResponse> getSearchMovie(@Query("api_key") String API_KEY, @Query("language") String language ,@Query("query") String judul);}