package com.meirusfandi.searchmovie.data;import android.os.Parcel;import android.os.Parcelable;import com.google.gson.annotations.SerializedName;public class MovieModel implements Parcelable{    @SerializedName("id")    private int id;    @SerializedName("title")    private String title;    @SerializedName("release_date")    private String release;    @SerializedName("overview")    private String overview;    @SerializedName("poster_path")    private String poster;    @SerializedName("backdrop_path")    private String backdrop;    @SerializedName("popularity")    private Long popularity;    @SerializedName("vote_averae")    private Double rating;    public MovieModel(){    }    public MovieModel(int id, String title, String release, String overview, String poster, String backdrop, Long popularity, Double rating) {        this.id = id;        this.title = title;        this.release = release;        this.overview = overview;        this.poster = poster;        this.backdrop = backdrop;        this.popularity = popularity;        this.rating = rating;    }    public MovieModel(String title, String release, String overview, String poster, String backdrop, Long popularity, Double rating) {        this.title = title;        this.release = release;        this.overview = overview;        this.poster = poster;        this.backdrop = backdrop;        this.popularity = popularity;        this.rating = rating;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getRelease() {        return release;    }    public void setRelease(String release) {        this.release = release;    }    public String getOverview() {        return overview;    }    public void setOverview(String overview) {        this.overview = overview;    }    public String getPoster() {        return poster;    }    public void setPoster(String poster) {        this.poster = poster;    }    public String getBackdrop() {        return backdrop;    }    public void setBackdrop(String backdrop) {        this.backdrop = backdrop;    }    public Long getPopularity() {        return popularity;    }    public void setPopularity(Long popularity) {        this.popularity = popularity;    }    public Double getRating() {        return rating;    }    public void setRating(Double rating) {        this.rating = rating;    }    private MovieModel(Parcel in) {        id = in.readInt();        title = in.readString();        release = in.readString();        overview = in.readString();        poster = in.readString();        backdrop = in.readString();        if (in.readByte() == 0) {            popularity = null;        } else {            popularity = in.readLong();        }        if (in.readByte() == 0) {            rating = null;        } else {            rating = in.readDouble();        }    }    public static final Parcelable.Creator<MovieModel> CREATOR = new Parcelable.Creator<MovieModel>() {        @Override        public MovieModel createFromParcel(Parcel in) {            return new MovieModel(in);        }        @Override        public MovieModel[] newArray(int size) {            return new MovieModel[size];        }    };    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel dest, int flags) {        dest.writeInt(id);        dest.writeString(title);        dest.writeString(release);        dest.writeString(overview);        dest.writeString(poster);        dest.writeString(backdrop);        if (popularity == null) {            dest.writeByte((byte) 0);        } else {            dest.writeByte((byte) 1);            dest.writeLong(popularity);        }        if (rating == null) {            dest.writeByte((byte) 0);        } else {            dest.writeByte((byte) 1);            dest.writeDouble(rating);        }    }}