package com.meirusfandi.searchmovie.views;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.ImageButton;import android.widget.ImageView;import android.widget.TextView;import com.meirusfandi.searchmovie.BuildConfig;import com.meirusfandi.searchmovie.R;import com.squareup.picasso.Picasso;import butterknife.BindView;import butterknife.ButterKnife;public class DetailActivity extends AppCompatActivity {    public static String EXTRA_ID = "extra_id";    public static String EXTRA_TITLE = "extra_title";    public static String EXTRA_RELEASE = "extra_release";    public static String EXTRA_OVERVIEW = "extra_overview";    public static String EXTRA_POSTER = "extra_poster";    public static String EXTRA_BACKDROP = "extra_backdrop";    public static String EXTRA_RATING = "extra_rating";    public static String EXTRA_POPULARITY = "extra_popularity";    @BindView(R.id.backdrop)    ImageView backdrop;    @BindView(R.id.fav)    ImageButton fav;    @BindView(R.id.title)    TextView title;    @BindView(R.id.rating)    TextView rating;    @BindView(R.id.popularity)    TextView popularity;    @BindView(R.id.release)    TextView release;    @BindView(R.id.overview)    TextView overview;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_detail);        ButterKnife.bind(this);        title.setText(getIntent().getStringExtra(EXTRA_TITLE));        release.setText(getIntent().getStringExtra(EXTRA_RELEASE));        overview.setText(getIntent().getStringExtra(EXTRA_OVERVIEW));        rating.setText(getIntent().getStringExtra(EXTRA_RATING));        popularity.setText(getIntent().getStringExtra(EXTRA_POPULARITY));        fav.setClickable(false);        Picasso.with(this)                .load(BuildConfig.BACKDROP_PATH+getIntent().getStringExtra(EXTRA_BACKDROP))                .placeholder(getResources().getDrawable(R.drawable.ic_launcher_foreground))                .error(getResources().getDrawable(R.drawable.ic_launcher_foreground))                .into(backdrop);    }}