package com.meirusfandi.searchmovie.views;import android.app.ProgressDialog;import android.support.v4.widget.SwipeRefreshLayout;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import com.meirusfandi.searchmovie.BuildConfig;import com.meirusfandi.searchmovie.R;import com.meirusfandi.searchmovie.data.MovieClients;import com.meirusfandi.searchmovie.data.MovieInterface;import com.meirusfandi.searchmovie.data.MovieModel;import com.meirusfandi.searchmovie.data.MovieResponse;import com.meirusfandi.searchmovie.helper.MovieAdapter;import java.util.ArrayList;import java.util.List;import butterknife.BindView;import butterknife.ButterKnife;import retrofit2.Call;import retrofit2.Callback;import retrofit2.Response;public class MainActivity extends AppCompatActivity {    @BindView(R.id.etSearch)    EditText etSearch;    @BindView(R.id.btnSearch)    Button btnSearch;    @BindView(R.id.recycler)    RecyclerView recyclerView;    @BindView(R.id.swipe)    SwipeRefreshLayout swipe;    MovieAdapter movieAdapter;    List<MovieModel> movieModels;    MovieInterface endPoint;    Call<MovieResponse> movieResponseCall;    private static final String API_KEY = BuildConfig.API_KEY;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        ButterKnife.bind(this);        movieModels = new ArrayList<>();        movieAdapter = new MovieAdapter(movieModels, this);        movieAdapter.notifyDataSetChanged();        recyclerView.setHasFixedSize(true);        recyclerView.setLayoutManager(new LinearLayoutManager(this));        recyclerView.setAdapter(movieAdapter);        swipe.setColorScheme(android.R.color.holo_blue_bright,                android.R.color.holo_green_light,                android.R.color.holo_orange_light,                android.R.color.holo_red_light);        swipe.setOnRefreshListener(new SwipeRefreshLayout.OnRefreshListener() {            @Override            public void onRefresh() {                swipe.setRefreshing(false);                getData();            }        });        btnSearch.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                getData();            }        });    }    public void getData(){        final ProgressDialog dialog = new ProgressDialog(this);        dialog.setMessage("Searching Movie, Please Wait .... ");        dialog.setCancelable(false);        dialog.show();        String judul = etSearch.getText().toString();        endPoint = MovieClients.getClient().create(MovieInterface.class);        movieResponseCall = endPoint.getSearchMovie(API_KEY, "&language=en-US&query="+judul);        movieResponseCall.enqueue(new Callback<MovieResponse>() {            @Override            public void onResponse(Call<MovieResponse> call, Response<MovieResponse> response) {                movieModels = response.body().getMovieModels();                movieAdapter = new MovieAdapter(movieModels, getApplicationContext());                movieAdapter.notifyDataSetChanged();                recyclerView.setAdapter(movieAdapter);                Toast.makeText(getApplicationContext(), "Fetching data from API Service has been Successfully", Toast.LENGTH_LONG).show();                dialog.dismiss();            }            @Override            public void onFailure(Call<MovieResponse> call, Throwable t) {                dialog.dismiss();                Toast.makeText(getApplicationContext(), "Error while getting data", Toast.LENGTH_LONG).show();            }        });    }}